const fs = require('fs');
const path = require('path');
const {app} = require('electron');
const lnk = require('./icons');
const is = require('electron-is');

const {getIconForPath, ICON_SIZE_MEDIUM} = require('system-icon');
const allowedExt = ['.exe', '.app', '.url'];
const pathList = {
    Desktop: path.join(app.getPath('home'), 'Desktop/'),
    Applications: path.normalize("/Applications/"),
};

exports.run = function(typie) {
    return new Promise((resolve, reject) => {
        let results = [];

        typie.getFilesList(allowedExt, pathList).go()
            .then((data) => {
                let list = data.data;
                let resolveInterval = setInterval(() => {
                    if (results.length >= list.length) {
                        clearInterval(resolveInterval);
                        resolve(results);
                    }
                });

                for (let file of list) {
                    let fileName = path.basename(file.path, file.ext);
                    let item = {title: fileName, t: 'Applications', d: file.desc, p: file.path, i: defaultFileIco, db:'global', c:0};

                    getIconForPath(file.path, ICON_SIZE_MEDIUM, (err, bitmap) => {
                        if (err) {
                            console.error(err);
                        } else {
                            item.i = "data:image/png;base64," + base64_encode(bitmap);
                        }
                        results.push(item);
                    });
                }
            })
            .catch((err) => {
                console.log('error', err);
                reject(err);
            });
    });
};

function base64_encode(bitmap) {
    // convert binary data to base64 encoded string
    return new Buffer(bitmap).toString('base64');
}


function getRowFromPath (results, fileFull, fileExt, next) {
    let file = path.basename(fileFull);
    let basename = path.basename(file, fileExt);
    let description = '';

    let item = {title: basename, t: 'Files', d: description, path: fileFull, icon: defaultFileIco};

    if (fileExt === '.lnk' && is.windows()) {
        getIconFromLnk(fileFull, item, results, next);
    } else if (fileExt === '.url' && is.windows()) {
        item.icon = defaultUrlIco;
        getIconFromUrl(fileFull, item, results, next);
    } else {
        app.getFileIcon(fileFull, {size: 'normal'}, function (err, res) {
            if (err) {
                console.log(err);
                results.push(item);
                next();
            } else {
                item.icon = res.toDataURL();
                results.push(item);
                next();
            }
        })
    }
}

function getIconFromLnk (fileFull, item, results, next) {
    lnk.queryLnk(fileFull, function (err, iconFile, icon) {
        if (err) {
            console.log(err);
            results.push(item);
            next();
        } else {
            if (icon) {
                item.icon = icon;
                results.push(item);
                next();
            } else {
                app.getFileIcon(path.resolve(iconFile), {size: 'normal'}, function (err, res) {
                    if (err) {
                        console.log(err);
                    } else {
                        item.icon = res.toDataURL();
                    }
                    results.push(item);
                    next();
                })
            }
        }
    })
}

function getIconFromUrl (fileFull, item, results, next) {
    lnk.queryUrl(fileFull, (err, iconFile) => {
        if (err) {
            results.push(item);
            next();
        } else {
            app.getFileIcon(path.resolve(iconFile), {size: 'normal'}, function (err, res) {
                if (err) {
                    console.log(err);
                } else {
                    item.icon = res.toDataURL();
                }
                results.push(item);
                next();
            })
        }
    })
}

const defaultFileIco = 'data:image/png;base64,'
const defaultUrlIco = 'data:image/png;base64,'
